/* PRVI UPIT Izlistaj sve linije cija je dolazna stanica sarajevo */
select naziv_linije
from linije
where dolazna_stanica = 'Sarajevo';
 
/* DRUGI UPIT Izlistaj sve prevoznike cije je mjesto u Bihacu */
select naziv_prevoznika,
		mjesto,
		adresa
from prevoznici
where mjesto='Bihac';

/* TRECI UPIT PRODAJA KARATA Izlistaj datum putovanja, naziv prevoznika, naziv destinacije i cijenu karte gdje je cijena izmedju 20 i 50 KM.   */

select datum_putovanja,
		naziv_destinacije,
		naziv_prevoznika,
		cijena_karte
from prodaja_karata
where cijena_karte between 20 and 50;


/* CETVRTI UPIT Izlistaj mi destinaciju, cijenu gdje je cijena izmedju 30 i 50KM a destinacija pocinje na slovo T */

select destinacija,
		cijena_prevoza
from relacije r
where destinacija like 'T%' and  cijena_prevoza between 30 and 60;



/* PETI UPIT spajanje prevoznika i prodaje karata - izlistaj sve prodane karte za prevoznika Unatransport sortirane po nazivu destinacije odozdo*/

select p.naziv_prevoznika,
		d.datum_izdavanja,
		d.datum_putovanja,
        d.naziv_destinacije
from prevoznici p
left join prodaja_karata d
on p.naziv_prevoznika = d.naziv_prevoznika
where p.naziv_prevoznika= 'Unatransport'
order by naziv_destinacije desc;

/* SESTI UPIT */

select l.idLinije,
		r.idRelacije,
        r.destinacija,
        r.cijena_prevoza        
from relacije r
inner join linije l using (idLinije);

/* SEDMI UPIT */

select l.vrijeme_dolaska,
		l.naziv_prevoznika,
        r.cijena_prevoza,
        r.destinacija
from relacije r
right join linije l using(vrijeme_dolaska);



